package com.onebyte.eon.utilsimport android.app.Dialogimport android.content.Contextimport android.graphics.drawable.ColorDrawableimport android.util.Logimport android.view.Gravityimport android.view.WindowManagerimport com.onebyte.eon.Rimport javax.inject.Injectclass ProgressHUD @Inject constructor(context: Context) : Dialog(context, R.style.ProgressHUD) {    fun showLoadingDialog(): ProgressHUD {        setTitle("")        setContentView(R.layout.progress_hud)        window!!.setLayout(WindowManager.LayoutParams.MATCH_PARENT,            WindowManager.LayoutParams.MATCH_PARENT        )        setCancelable(false)        window!!.attributes.gravity = Gravity.CENTER        val lp = window!!.attributes        lp.dimAmount = 0.2f        window!!.attributes = lp        window!!.setBackgroundDrawable(ColorDrawable(0x7000000))        try {            show()        } catch (ex: Exception) {            Log.e("DIALOG_ERROR", ex.toString())        }        return this    }}